import base64, struct

b = struct.pack('>I', 10240099)
print('b=', b)

d = struct.unpack('>IH', b'\xf0\xf0\xf0\xf0\x80\x80')
print('d=', d)

s = b'\x42\x4d\x38\x8c\x0a\x00\x00\x00\x00\x00\x36\x00\x00\x00\x28\x00\x00\x00\x80\x02\x00\x00\x68\x01\x00\x00\x01\x00\x18\x00'
d = struct.unpack('<ccIIIIIIHH', s)
print('d=', d)

bmp_data = base64.b64decode('')

def bmp_info(data):
    d = struct.unpack('<ccIIIIIIHH', data[:30])
    print('d=', d)
    if d[:2] == (b'B', b'M'):
        return {
            'width': d[-4:][0],
            'height': d[-3:][0],
            'color': d[-1:][0]
        }
    return print('not BMP')

# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')